import React, { useEffect, useRef } from "react";
import mapboxgl from "mapbox-gl";
import "mapbox-gl/dist/mapbox-gl.css";

mapboxgl.accessToken =
  "pk.eyJ1IjoibWFwZmVhbiIsImEiOiJjbTNuOGVvN3cxMGxsMmpzNThzc2s3cTJzIn0.1uhX17BCYd65SeQsW1yibA";

const FROST_CLIENT_ID = "12f68031-8ce7-48c7-bc7a-38b843f53711";
const FROST_CLIENT_SECRET = "08a75b8d-ca70-44a9-807d-d79421c082bf";

const WeatherStationsMap = () => {
  const mapContainer = useRef(null);
  const mapRef = useRef(null);

  // --- ðŸ§¹ LINTING + CONVERSION ------------------------------------------------
  const stationsToGeoJSON = (stations) => {
    const stats = {
      total: stations.length,
      kept: 0,
      nonPoint: 0,
      badCoords: 0,
      outOfRange: 0,
      duplicates: 0,
    };

    const seen = new Set(); // dedupe by rounded lng/lat
    const features = [];

    for (const s of stations) {
      const g = s?.geometry;
      if (!g || g.type !== "Point") {
        stats.nonPoint++;
        continue;
      }

      let coords = g.coordinates;
      if (!Array.isArray(coords) || coords.length !== 2) {
        stats.badCoords++;
        continue;
      }

      let [lng, lat] = coords.map(Number);
      if (!Number.isFinite(lng) || !Number.isFinite(lat)) {
        stats.badCoords++;
        continue;
      }

      // WGS84 world bounds
      if (lng < -180 || lng > 180 || lat < -90 || lat > 90) {
        stats.outOfRange++;
        continue;
      }

      const key = `${lng.toFixed(6)},${lat.toFixed(6)}`;
      if (seen.has(key)) {
        stats.duplicates++;
        continue;
      }
      seen.add(key);

      features.push({
        type: "Feature",
        geometry: { type: "Point", coordinates: [lng, lat] },
        properties: {
          id: String(s.id ?? s.sourceId ?? ""),
          name: s.name ?? s.shortName ?? "Unknown",
          shortName: s.shortName ?? "",
          country: s.country ?? "Norway",
        },
      });
      stats.kept++;
    }

    console.log(
      `ðŸ§¹ Lint summary â€” total: ${stats.total}, kept: ${stats.kept}, ` +
        `removed â†’ nonPoint:${stats.nonPoint}, badCoords:${stats.badCoords}, ` +
        `outOfRange:${stats.outOfRange}, duplicates:${stats.duplicates}`
    );

    return { type: "FeatureCollection", features };
  };

  // --- ðŸŒ FETCH ---------------------------------------------------------------
  const fetchStations = async () => {
    try {
      const frostAuth = btoa(`${FROST_CLIENT_ID}:${FROST_CLIENT_SECRET}`);
      console.log("ðŸŒ Fetching stations from Frost API...");

      const res = await fetch("/frost/sources/v0.jsonld?types=SensorSystem", {
        headers: {
          Authorization: `Basic ${frostAuth}`,
          Accept: "application/json",
        },
      });

      console.log("ðŸ”„ Response status:", res.status);
      if (!res.ok) throw new Error(`Frost error ${res.status}`);

      const data = await res.json();
      if (!data || !Array.isArray(data.data)) {
        console.error("ðŸš¨ Unexpected Frost payload shape");
        return [];
      }
      return data.data;
    } catch (e) {
      console.error("ðŸš¨ Fetch error:", e);
      return [];
    }
  };

  // --- ðŸ—ºï¸ MAP -----------------------------------------------------------------
  useEffect(() => {
    const init = async () => {
      if (mapRef.current) return;

      console.log("ðŸ—ºï¸ Initializing Mapbox map...");
      const map = new mapboxgl.Map({
        container: mapContainer.current,
        style: "mapbox://styles/mapbox/streets-v11",
        center: [10.75, 59.91],
        zoom: 5,
      });
      mapRef.current = map;

      await new Promise((r) => map.on("load", r));
      console.log("ðŸ›°ï¸ Mapbox loaded");

      const stations = await fetchStations();
      console.log(`ðŸ“Š Raw stations fetched: ${stations.length}`);

      const geojson = stationsToGeoJSON(stations);
      console.log("ðŸŒ GeoJSON ready:", geojson);

      // Use a Blob URL to avoid inline-JSON parsing limits
      const blob = new Blob([JSON.stringify(geojson)], {
        type: "application/json",
      });
      const url = URL.createObjectURL(blob);

      // Source with clustering enabled
      if (!map.getSource("stations")) {
        map.addSource("stations", {
          type: "geojson",
          data: url,
          cluster: true,
          clusterRadius: 40, // px
          clusterMaxZoom: 12, // highest zoom to cluster points
        });
      }

      // Cluster circles
      if (!map.getLayer("clusters")) {
        map.addLayer({
          id: "clusters",
          type: "circle",
          source: "stations",
          filter: ["has", "point_count"],
          paint: {
            "circle-color": [
              "step",
              ["get", "point_count"],
              "#ffb347", // small
              20,
              "#ff7f50", // medium
              100,
              "#ff5c5c", // large
            ],
            "circle-radius": [
              "step",
              ["get", "point_count"],
              14,
              20,
              18,
              100,
              24,
            ],
            "circle-stroke-width": 1,
            "circle-stroke-color": "#fff",
          },
        });
      }

      // Cluster count labels
      if (!map.getLayer("cluster-count")) {
        map.addLayer({
          id: "cluster-count",
          type: "symbol",
          source: "stations",
          filter: ["has", "point_count"],
          layout: {
            "text-field": ["get", "point_count_abbreviated"],
            "text-font": ["DIN Pro Medium", "Arial Unicode MS Bold"],
            "text-size": 12,
          },
          paint: {
            "text-color": "#222",
          },
        });
      }

      // Unclustered points
      if (!map.getLayer("unclustered-point")) {
        map.addLayer({
          id: "unclustered-point",
          type: "circle",
          source: "stations",
          filter: ["!", ["has", "point_count"]],
          paint: {
            "circle-radius": [
              "interpolate",
              ["linear"],
              ["zoom"],
              0, 3,
              6, 5,
              10, 7,
              15, 12,
            ],
            "circle-color": "#ff6600",
            "circle-stroke-width": 1,
            "circle-stroke-color": "#fff",
            "circle-opacity": 0.9,
          },
        });
      }

      console.log("âœ… Cluster + point layers added");

      // Smooth zoom animation into a cluster
      map.on("click", "clusters", (e) => {
        const features = map.queryRenderedFeatures(e.point, {
          layers: ["clusters"],
        });
        const clusterId = features[0].properties.cluster_id;
        const coordinates = features[0].geometry.coordinates;
        const source = map.getSource("stations");

        source.getClusterExpansionZoom(clusterId, (err, zoom) => {
          if (err) return;
          map.easeTo({
            center: coordinates,
            zoom,
            duration: 600,
            essential: true,
          });
        });
      });

      // Popup for a single station
      map.on("click", "unclustered-point", (e) => {
        const f = e.features?.[0];
        if (!f) return;
        const { id, name, country } = f.properties;
        new mapboxgl.Popup({ closeOnMove: true })
          .setLngLat(f.geometry.coordinates)
          .setHTML(
            `<strong>${name}</strong><br/>ID: ${id}<br/>Country: ${country}`
          )
          .addTo(map);
      });

      // Pointer cursor on hover
      const pointerLayers = ["clusters", "unclustered-point"];
      pointerLayers.forEach((layerId) => {
        map.on("mouseenter", layerId, () => {
          map.getCanvas().style.cursor = "pointer";
        });
        map.on("mouseleave", layerId, () => {
          map.getCanvas().style.cursor = "";
        });
      });

      // Fit to region (Norway-ish)
      map.fitBounds(
        [
          [4, 57],
          [31, 72],
        ],
        { padding: 20, duration: 700 }
      );

      // Explicit render errors
      map.on("error", (e) => console.error("ðŸ›‘ Mapbox error:", e?.error));
    };

    init();

    // Optional cleanup on unmount
    return () => {
      if (mapRef.current) {
        mapRef.current.remove();
        mapRef.current = null;
      }
    };
  }, []);

  return (
    <div
      ref={mapContainer}
      style={{ width: "100%", height: "100vh", borderRadius: "10px" }}
    />
  );
};

export default WeatherStationsMap;
